    def get_estimated_ph(self):
        L.sort(key=lambda item: ((int(item[0]) - int(self.ph[0]))**2 + (int(item[1]) - int(self.ph[1]))**2 + (int(item[2]) - int(self.ph[2]))**2)**.5)
